try:
    import pandas as pd
except:
    pd = None


def set_pandas_output_option():
    if pd:
        pd.set_option('display.float_format', lambda x: '%.3f' % x)
        pd.set_option('display.max_rows', 500)
        pd.set_option('display.max_columns', 500)
        pd.set_option('display.width', 1000)
